import React,{Component} from 'react'
import classnames from 'classnames'
import './Toolbar.css'
import {Link} from 'react-router-dom'
import facebook from '../../social_media_images/facebook.png'
import twitter from '../../social_media_images/twitter.png'
import linkedin from '../../social_media_images/linkedin.png'
import DrawerToggleButton from '../SideDrawer/DrawerToggleButton'
class Toolbar extends Component{
    constructor(props){
        super(props)
        this.state={
            visible: true
        }
    }
    componentDidMount() {
        window.addEventListener("scroll", this.handleScroll);
    }

      // Remove the event listener when the component is unmount.
    componentWillUnmount() {
        window.removeEventListener("scroll", this.handleScroll);
    }
    handleScroll = () => {

        const currentScrollPos = window.pageYOffset;
        const visible = currentScrollPos===0;

        this.setState({
          visible
        })
    }
    render(){
    return (
        <header className={classnames("toolbar", {
            "toolbar--hidden": !this.state.visible
          })}
          >
            <nav className="toolbar_navigation">
                <div className="toolbar_toggle-button">
                    <DrawerToggleButton click={this.props.drawerClickHandler}/>
                </div>
                <div className="toolbar_logo"><Link to="/">THE LOGO</Link></div>
                <div className="spacer"/>
                <div className="toolbar_navigation-items">
                    <ul>
                        <li><Link activeClassName="nav_link--active" to="/">Home</Link></li>
                        <li><Link activeClassName="nav_link--active" to="/features">Features</Link></li>
                        <li><Link activeClassName="nav_link--active" to="/about">About Us</Link></li>
                        <li><Link activeClassName="nav_link--active" to="/contact">Contact</Link></li>
                        <li>
                            <a href="https://facebook.com" target="blank"><img src={facebook} alt="Facebook"/></a>
                            <a href="https://twitter.com" target="blank"><img style={{paddingLeft:'0.35em'}} src={twitter} alt="Twitter"/></a>
                            <a href="https://linkedin.com" target="blank"><img style={{paddingLeft:'0.35em'}} src={linkedin} alt="LinkedIn"/></a>
                        </li>
                        </ul>
                </div>
            </nav>
        </header>
    )
    }
}
export default Toolbar;
